    input clk,
    input reset_n,

    output reg n_blank,
    output reg n_sync,
    output reg h_sync,
    output reg v_sync,
    output [31:0] row,
    output [31:0] column
);

// Define the VGA timings
parameter HACTIVE = 96; // Horizontal active pixels per line
parameter VACTIVE = 60; // Vertical active lines

// Define the VGA timings
parameter PCLK_PERIOD = 25.175; // Pixel clock period in ns
parameter HACTIVE_TIME = (PCLK_PERIOD * HACTIVE);
parameter VACTIVE_TIME = (PCLK_PERIOD * VACTIVE);

// Define the reset sequence
always @(posedge clk or negedge reset_n) begin
    if (!reset_n)
        #1;
    else begin
        // Reset all outputs to zero
        n_blank <= 0;
        n_sync <= 0;
        h_sync <= 0;
        v_sync <= 0;

        // Initialize row and column counters
        row <= 0;
        column <= 0;
    end
end

// Generate horizontal sync signal
always @(posedge clk) begin
    if (column == HACTIVE - 1)
        h_sync <= 1'b1;
    else
        h_sync <= 1'b0;
end

// Generate vertical sync signal
always @(posedge clk) begin
    if (row == VACTIVE - 1)
        v_sync <= 1'b1;
    else
        v_sync <= 1'b0;
end

